export default function Logo({ name, className = "", size = 24 }: LogoProps) {
  const logoElement = logoComponents[name];

  if (!logoElement) {
    return null;
  }

  return (
    <div className={className} style={{ width: size, height: size }}>
      {React.cloneElement(logoElement, {
        width: size,
        height: size,
        className: "w-full h-full",
      })}
    </div>
  );
}
